import { createSlice } from "@reduxjs/toolkit";

const usersReducer = createSlice({
    name: 'users',
    initialState: {
        users: [
            { id: 1, name: 'Asif' },
            { id: 2, name: 'Asif Gul' },
            { id: 3, name: 'Laila' }
        ],
    },
    reducers: {
        addUser: (state, action) => {

        }
    },
})
export const { addUser } = usersReducer.actions;
export default usersReducer.reducer;
