import { createSlice } from "@reduxjs/toolkit";
import { listUsers, singleUser } from '../actions/userActions';
const usersReducer = createSlice({
    name: 'users',
    initialState: {
        users: [],
        usersContainer: [],
        errors: {},
        loader: false,
        user: {}
    },
    reducers: {
        filteredUser: (state, action) => {

            state.users = state.users.filter((user) => user.name.toLowerCase().includes(action.payload))

        },
    },
    extraReducers: {
        [listUsers.pending]: (state, action) => {
            state.loader = true
        },
        [listUsers.fulfilled]: (state, action) => {
            state.loader = false
            state.users = action.payload
        },
        [listUsers.rejected]: (state, action) => {
            state.loader = false
            state.errors = action.payload
        },
        [singleUser.pending]: (state, action) => {
            state.loader = true
        },
        [singleUser.fulfilled]: (state, action) => {
            state.loader = false
            state.user = action.payload
        },
        [singleUser.rejected]: (state, action) => {
            state.loader = false
            state.errors = action.payload
        }
    },
})
export const { filteredUser } = usersReducer.actions;
export default usersReducer.reducer;
