import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { useRef, useEffect } from 'react';
import { addUser } from '../store/reducers/userReducer'
import { listUsers } from '../store/actions/userActions';
import Loader from '../components/Loader';
import Filter from '../components/Filter';
const UserHome = () => {
    const { users, loader } = useSelector(state => state.users);
    const dispatch = useDispatch();
    // const inputRef = useRef('');
    // const formSubmit = (e) => {
    //     e.preventDefault();
    //     // console.log(inputRef.current.value)
    //     // dispatch(addUser(inputRef.current.value));
    //     // inputRef.current.value = '';

    // }
    useEffect(() => {
        dispatch(listUsers());
    }, [])

    return !loader ? (
        <>
            <Filter />
            <table className="table">
                <thead>
                    <tr>
                        <th>Sno</th>
                        <th>Name</th>
                        <th>Username</th>
                        <th>Email</th>
                        <th>Details</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user, index) => (
                        <tr key={index}>
                            <td>{index + 1}</td>
                            <td>{user.name}</td>
                            <td>{user.username}</td>
                            <td>{user.email}</td>
                            <td><Link to={`/details/${user.id}`} className='btn btn-outline-success'>Details</Link></td>
                        </tr>
                    ))}
                </tbody >
            </table > </>) : (<Loader />)

    // <form onSubmit={formSubmit}>
    //     <input type='text' name='name' placeholder='Enter User Names' ref={inputRef} /><br /><br />
    //     <input type='submit' className='btn btn-submit' value='Submit' />
    // </form>
}

export default UserHome
