import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { useRef, useEffect } from 'react';
import { addUser } from '../store/reducers/userReducer'
import { listUsers } from '../store/actions/userActions';

const UserHome = () => {
    const { users, loader } = useSelector(state => state.users);
    console.log(loader)
    const dispatch = useDispatch();
    // const inputRef = useRef('');
    // const formSubmit = (e) => {
    //     e.preventDefault();
    //     // console.log(inputRef.current.value)
    //     // dispatch(addUser(inputRef.current.value));
    //     // inputRef.current.value = '';

    // }
    useEffect(() => {
        dispatch(listUsers());
    }, [])

    return !loader ? <table className="table">
        <thead>
            <tr>
                <th>Sno</th>
                <th>Name</th>
                <th>Username</th>
                <th>Email</th>
                <th>Details</th>
            </tr>
        </thead>
        <tbody>
            {users.map((user, index) => {
                <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{user.name}</td>
                    <td>{user.username}</td>
                    <td>{user.email}</td>
                    <td><Link to='/'>Details</Link></td>
                </tr>
            })}
        </tbody>
    </table> :
        <div className="text-center">
            <div className="spinner-border" role="status">
                <span className="visually-hidden">Loading...</span>
            </div>
        </div>

    // <form onSubmit={formSubmit}>
    //     <input type='text' name='name' placeholder='Enter User Names' ref={inputRef} /><br /><br />
    //     <input type='submit' className='btn btn-submit' value='Submit' />
    // </form>
}

export default UserHome
